mapper.select:
select

from workorder


        and workNum like CONCAT ('%',#{workNum},'%')


        and orderNum like CONCAT ('%',#{orderNum},'%')


        and productNum=#{productNum}


        and packageId=#{packageId}


        and sName=#{sName}


        and sTel=#{sTel}


        and sPoint=#{sPoint}


        and sCity=#{sCity}


        and gName=#{gName}


        and gTel=#{gTel}


        and gCity=#{gCity}


        and gPoint=#{gPoint}


        and workStatus=#{workStatus}


        and gCourier=#{gCourier}


        and sCourier=#{sCourier}


        and result=#{result}

select

from workorder


        and workNum like CONCAT ('%',#{workNum},'%')


        and orderNum like CONCAT ('%',#{orderNum},'%')


        and productNum=#{productNum}


        and packageId=#{packageId}


        and workStatus=#{workStatus}


        and storageStatus=#{storageStatus}


        and (sPoint=#{wdName} or gPoint=#{wdName})

select

from workorder
where 1=1

    and sCourier=#{sCourier}


    and workNum like CONCAT ('%',#{workNum},'%')


    and orderNum like CONCAT ('%',#{orderNum},'%')


    and productNum=#{productNum}


    and packageId=#{packageId}


    and sName=#{sName}


    and sTel=#{sTel}


    and sPoint=#{sPoint}


    and sCity=#{sCity}


    and gName=#{gName}


    and gTel=#{gTel}


    and gCity=#{gCity}


    and gPoint=#{gPoint}



    and gCourier=#{gCourier}



    and result=#{result}
